import React from 'react'

function Display(props) {
    const {numbers} = props
    let numbers_ = numbers.join('')
    let display =''
    let subString = ''
    console.log(numbers)
    console.log(`numbers_ : ${numbers_}`)
    let len =numbers.length
    let div = Math.floor(len / 3)

    let mod = len % 3
    console.log(mod)

    switch(div){
        case 0 : 
                    display = numbers_;break;


        case 1 : switch(mod){
                    case 0 : display  = numbers_;break;
                    case 1 : display  = numbers_.substring(0,1)+','+ numbers_.substring(1,len);break;
                    case 2 :  display  = numbers_.substring(0,mod)+','+ numbers_.substring(mod,len);break; 
                    default : break;
                    }break;


        case 2 : 
                 switch(mod){
                    case 0 : display  = numbers_.substring(0,3)+','+ numbers_.substring(1,len);break;
                    case 1 : display  = numbers_.substring(0,1)+','+ numbers_.substring(1,3)+','+ numbers_.substring(3,len);break;
                    case 2 :  display  = numbers_.substring(0,mod)+','+ numbers_.substring(mod,3)+','+ numbers_.substring(mod+3,len);break; 
                    default : break;
                }break;

        default: break;
    }

    return (
        <div className='display glass'>
        {display}<div className='preview'>preview</div>
      </div>
    )
}

export default Display
