import 'css-doodle'
import './App.css'
import React from 'react'
import Navigation from './components/Navigation'
import Calculator from './components/Calculator'
import formatter from './helpers/formatter'
import resolver from './helpers/resolver'
import data from './helpers/data'
import { create, all } from 'mathjs'

const config = { }
const math = create(all, config)

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      display: [],
      evaluate: [],
      numbers:[],
      result: 0
    }
    this.isClicked = this.isClicked.bind(this)
  }

  isClicked(value,className,number){

    if(isNaN(number)){
      resolver(className)
      if(className.includes('result')){
        const doMath = math.evaluate(this.state.evaluate.join('').trim())
        const numArray = String(doMath).split('').map((doMath)=>{
          return Number(doMath)
        })

        this.setState({
          numbers:[formatter(numArray)],
          evaluate:[...this.state.evaluate,' = ',doMath]
        })
      }else{
      // console.log(this.state.numbers)
      // const nums = parseInt(this.state.numbers.join(''))
      // console.log(nums)
      this.setState({
        numbers:[],
        evaluate:[...this.state.evaluate,' ',value,' ']
      })
    }

    }else{
      this.setState({
        numbers:[...this.state.numbers,number],
        evaluate:[...this.state.evaluate,number]
      })
    }
  }

  render() {
    const skeleton = data
    const numbers = this.state.numbers
    const evaluate  = this.state.evaluate
    const format = formatter
    return (
      <>
      <Navigation />
      <Calculator skeleton={skeleton} isClicked={this.isClicked} numbers={numbers} evaluate={evaluate} format={format}/>
    </>
    )
  }
}

export default App
