import React from 'react'

class Button extends React.Component{
  constructor(props){
    super(props)
    this.state={
      type: this.props.type
    }
  }

  componentDidUpdate(){
    this.props.operation(this.state.type)
  }

  render(){
    const { type, value, symbol, className, input } = this.props
  return (
    <button type ={type} value={value} className={className} onClick={input}>
      {symbol}
    </button>
  )
  }
}

export default Button
