import React from 'react'

function Display(props) {
    const {numbers} = props
    // let display = new String(numbers.join(''))
    console.log(numbers)
    let numbers_ = numbers.slice()
    let display ='' 
    let display_ = new String(numbers.join(''))
    switch (numbers.length){
        case 3: display = new String (numbers_.splice(3,0,","));break;
        case 6: display = new String (numbers_.splice(6,0,","));break;
        case 9: display = new String (numbers_.splice(9,0,","));break;
        case 12: display = new String (numbers_.splice(12,0,","));break;
        default :  display  = display_;break;
    }



    return (
        <div className='display glass'>
        {display}<div className='preview'>preview</div>
      </div>
    )
}

export default Display
