import 'css-doodle'
import './App.css'
import React from 'react'
import Navigation from './components/Navigation'
import Calculator from './components/Calculator'
import formatter from './helpers/formatter'
import data from './helpers/data'


class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      display: [],
      evaluate: [],
      numbers:[]
    }
    this.isClicked = this.isClicked.bind(this)
  }

  isClicked(value,className,number){

    if(isNaN(number)){

      if(className.includes('result')){
        const doMath = this.state.evaluate.join('').trim()
        console.log('Math')
        console.log(doMath)

      }
      console.log(this.state.numbers)
      const nums = parseInt(this.state.numbers.join(''))
      console.log(nums)
      this.setState({
        numbers:[],
        evaluate:[...this.state.evaluate,' ',value,' ']
      })

    }else{
      this.setState({
        numbers:[...this.state.numbers,number],
        evaluate:[...this.state.evaluate,number]
      })
    }
  }

  render() {
    const skeleton = data
    const numbers = this.state.numbers
    const evaluate  = this.state.evaluate
    const format = formatter
    return (
      <>
      <Navigation />
      <Calculator skeleton={skeleton} isClicked={this.isClicked} numbers={numbers} evaluate={evaluate} format={format}/>
    </>
    )
  }
}

export default App
