import 'css-doodle'
import './App.css'
import React from 'react'
import Navigation from './components/Navigation'
import Calculator from './components/Calculator'
import {Decimal} from 'decimal.js'
// import formatter from './helpers/formatter'

import data from './helpers/data'
import { create, all } from 'mathjs'

const config = { }
const math = create(all, config)

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      display: "",
      number:"",
      evaluate: [],
      numbers:[],
      result: 0,
      prevNums: [],
      prevRes: 0
    }

  }

  input =(e)=>{
    const [input,type] = e.target.value.split(',')
    if(type === "number"){

      const x = (input === ".") ? new Decimal(`0.${input}`) : new Decimal(input)
      this.setState({
        numbers : this.state.numbers.concat(input),
        number: this.state.number.concat(x)
      })
    }else{
      this.setState({
        display: this.state.display.concat(this.state.numbers.join(''))
      })
    }   
  }

  operation=(type)=>{
    console.log(type)
  }


  render() {
    const buttons = data
    const numbers = this.state.numbers
    return (
      <>
      <Navigation />
      <Calculator 
        buttons={buttons} 
        numbers={numbers} 
        input={this.input} 
        operation={this.operation} 
        display={this.state.display} 
        number={this.state.number}
        />
    </>
    )
  }
}

export default App
