import 'css-doodle'
import './App.css'
import React from 'react'
import Navigation from './components/Navigation'
import Calculator from './components/Calculator'
import {Decimal} from 'decimal.js'
import operation from './helpers/operation'
// import formatter from './helpers/formatter'

import data from './helpers/data'
import { create, all } from 'mathjs'

const config = { }
const math = create(all, config)

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      display: "",
      number:"",
      evaluate: "",
      numbers:[],
      result: "",
      prevNum: "",
      prevEva: "",
      prevRes: ""
    }

  }

  input =(e)=>{
    const [input,type] = e.target.value.split(',')
    let result = 0
    let expression = [0,false]

    if(type === "number"){
        this.setState({
          //numbers : this.state.numbers.concat(input),
          number: this.state.number.concat(input),
          display: this.state.number.concat(input),
          evaluate: this.state.prevEva.concat(input),
          prevEva: this.state.evaluate.concat(input)
        })

    }else{
      const evaluate = this.state.prevEva.concat(` ${input} `)
      console.log("EVALUATE", evaluate)
      const number = this.state.number || this.state.prevRes
      const prevEva = this.state.prevEva
      const prevRes =this.state.prevRes
      console.log("NUMBER", number)
      expression = operation(input,type,number,prevEva,prevRes);

     if (!expression[1]){
      this.setState({
        display: this.state.display.concat(` ${input} `),
        numbers: this.state.numbers.concat(parseFloat(this.state.number)),
        prevNum: this.state.number,
        prevEva: evaluate,
        evaluate: evaluate,
        number: ""
      })
     }else{
      console.log('expression is complete, lets evaluate')
      result = math.evaluate(expression[0])
      console.log("result", result)


      this.setState({
        display: result.toString(),
        numbers: this.state.numbers.concat(parseFloat(this.state.number)),
        prevNum: this.state.number,
        prevEva: expression[0],
        evaluate: expression[0],
        number: "",
        prevRes: result.toString(),
        result: result.toString()

      })


     }

    }   
  }


  render() {
    const buttons = data
    // const numbers = this.state.numbers
    // const evaluate = this.state.evaluate
    const {display, evaluate, number , numbers, prevEva, prevNum, prevRes, result} = this.state
    return (
      <>
      <Navigation />
      <Calculator 
        buttons={buttons} 
        numbers={numbers} 
        input={this.input} 
        operation={this.operation} 
        display={display}
        evaluate={evaluate}
        number={number}
        numbers={numbers}
        prevEva={prevEva}
        prevNum={prevNum}
        prevRes={prevRes}
        result={result}
        />
    </>
    )
  }
}

export default App
